import { createSlice } from "@reduxjs/toolkit";

const intialState ={
    value:0
}

const CounterSlice = createSlice( {
    name:" counter", 
    intialState,
    reducers:{
        increment : (state) => {
            state.value += 1;
        }
    }
})
